var WebSocketServer = require('ws').Server;
var UUID = require('node-uuid');
var events = require('events');
var util = require('util');
var errorCb = function(rtc) {
	return function(error) {
		if (error) {
			rtc.emit("error", error);
		}
	};
};

function SkyRTC() {
	this.sockets = [];
	this.rooms = {};
	this.on('__join', function(data, socket) {
		console.log(this.sockets.length);
		var ids = [],
			i, m,
			room = data.room || "__default",
			curSocket,
			curRoom;

		curRoom = this.rooms[room] = this.rooms[room] || [];
		console.log("现在的roomNum==="+data.room+", 现在的curRoom长度是 "+curRoom.length);

		for (i = 0, m = curRoom.length; i < m; i++) {
			curSocket = curRoom[i];
			if (curSocket.id === socket.id) {
				continue;
			}
			ids.push(curSocket.id);
			curSocket.send(JSON.stringify({
				"eventName": "_new_peer",
				"data": {
					"socketId": socket.id
				}
			}), errorCb);
		}

		curRoom.push(socket);
		socket.room = room;

		socket.send(JSON.stringify({
			"eventName": "_peers",
			"data": {
				"connections": ids,
				"you": socket.id
			}
		}), errorCb);

		this.emit('new_peer', socket, room);
	});

	this.on('__ice_candidate', function(data, socket) {
		var soc = this.getSocket(data.socketId);

		if (soc) {
			soc.send(JSON.stringify({
				"eventName": "_ice_candidate",
				"data": {
					"label": data.label,
					"candidate": data.candidate,
					"socketId": socket.id
				}
			}), errorCb);

			this.emit('ice_candidate', socket, data);
		}
	});

	this.on('__offer', function(data, socket) {
		var soc = this.getSocket(data.socketId);

		if (soc) {
			soc.send(JSON.stringify({
				"eventName": "_offer",
				"data": {
					"sdp": data.sdp,
					"socketId": socket.id
				}
			}), errorCb);
		}
		this.emit('offer', socket, data);
	});

	this.on('__answer', function(data, socket) {
		var soc = this.getSocket(data.socketId);
		if (soc) {
			soc.send(JSON.stringify({
				"eventName": "_answer",
				"data": {
					"sdp": data.sdp,
					"socketId": socket.id
				}
			}), errorCb);
			this.emit('answer', socket, data);
		}
	});
	
	//从服务器中移除此room,把客户端和服务器连接的socket断掉
	this.on('__remove_room',function(roomNum){
	    var that = this;
		var i,m,curRoom,curSocket;
		console.log('此时要__remove_room是 '+roomNum);
		curRoom = this.rooms[roomNum]=this.rooms[roomNum] || [];
		m = curRoom.length;
		if(roomNum && curRoom){
			curRoom = that.rooms[roomNum];
			for (i = curRoom.length; i--;) {
				curRoom[i].send(JSON.stringify({
					"eventName": "_remove_room_peer",
					"data": {
						"socketId": curRoom[i].id
					}
				}), errorCb);
			}
		}
		that.removeRoomPeers(roomNum);

		this.rooms[roomNum].splice(0,m-1);
		if (this.rooms[roomNum].length === 0) {
			delete this.rooms[roomNum];
		}
		that.emit('remove_room',roomNum);
/*
		if(roomNum && curRoom){
			for (i = 0; i < m; i++) {
				console.log("m=="+this.rooms[roomNum].length);
				console.log("此时i="+i+", socketid="+curRoom[i].id);
				curSocket = curRoom[i];
				if(curSocket){
					curSocket.send(JSON.stringify({
						"eventName": "_remove_room_peer",
						"data": {
							"socketId": curSocket.id
						}}), errorCb);
				that.emit('__remove_peer_socket', curSocket);
				}else{
					console.log('__remove_room中，此socket并不存在');
				}
			}
		}else{
			console.log('__remove_room, room或者curRoom为空');
		}
*/
	});
}

util.inherits(SkyRTC, events.EventEmitter);

SkyRTC.prototype.addSocket = function(socket) {
	this.sockets.push(socket);
};

//移除此房间所有成员
SkyRTC.prototype.removeRoomPeers = function(room){
	var that = this;
	var i,j,m,curRoom,curSocket;
	curRoom = this.rooms[room]=this.rooms[room] || [];
	m = curRoom.length;

	if(room && curRoom){
		for(i = 0; i < m; i++){
			curSocket = curRoom[i];
			if(curSocket){
				j = this.sockets.indexOf(curSocket);
				this.sockets.splice(j,1);
				that.emit('remove_peer', curSocket.id, that);
				console.log(curSocket.id+" 在this.sockets中已经被移除了");
			}
		}
	}
}

SkyRTC.prototype.removeSocket = function(socket) {
	var i = this.sockets.indexOf(socket),
		room = socket.room;
	this.sockets.splice(i, 1);
	if (room) {
		i = this.rooms[room].indexOf(socket);
		this.rooms[room].splice(i, 1);
		if (this.rooms[room].length === 0) {
			delete this.rooms[room];
		}
	}
};

SkyRTC.prototype.broadcast = function(data, errorCb) {
	var i;
	for (i = this.sockets.length; i--;) {
		this.sockets[i].send(data, errorCb);
	}
};

SkyRTC.prototype.broadcastInRoom = function(room, data, errorCb) {
	var curRoom = this.rooms[room],
		i;
	if (curRoom) {
		for (i = curRoom.length; i--;) {
			curRoom[i].send(data, errorCb);
		}
	}
};

SkyRTC.prototype.getRooms = function() {
	var rooms = [],
		room;
	for (room in this.rooms) {
		rooms.push(room);
	}
	return rooms;
};

SkyRTC.prototype.getSocket = function(id) {
	var i,
		curSocket;
	if (!this.sockets) {
		return;
	}
	for (i = this.sockets.length; i--;) {
		curSocket = this.sockets[i];
		if (id === curSocket.id) {
			return curSocket;
		}
	}
	return;
};

